package com.leetcode2;

import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;
import java.util.Map;

/**
 * https://leetcode.com/problems/minimum-window-subsequence/description/
 */
public class MinimumWindowSubsequence {

    public static void main(String[] args) {
        MinimumWindowSubsequence p = new MinimumWindowSubsequence();

        //String s = "abcdebdde", t = "bde";
        //String s = "jmeqksfrsdcmsiwvaovztaqenprpvnbstl", t = "u";
        //String s = "jmeqksfrsdcmsiwvaovztaqenprpvnbstl", t = "k";
        //String s = "abbcdefcab", t = "cc";
        //String s = "cnhczmccqouqadqtmjjzl", t = "mm";
        //String s = "cnhczmccqouqadqtmjjzl", t = "cm";
        //String s = "fweekpamjwqobhxiesgzivminqqjjkgnhkdxpfjvvgfcdlgwvwtdwizpjcuwnwpioxcshyjglqjnkluedopzyhozjzqnjentspwffoawwbgyhrrapncwetqulmaupkkwugkpfztgejujlakrnkvefbvncfzhhmciztzjzrzrzlcfkejmlkhlogtraexhgrvxitcnaacegjrkwbseomwvdawsymemhsvtqcpbfvinhngdvhnrswwgoff", t = "qkkwtlzbbn";
        String s = "", t = "fffessa";
        //String s = "", t = "tgpuesgnuxwgzzlomkgbswrwvpscidszzpsuhwjpuylgxrujoqhdomvdwitxkyshnemycdhvecygxcesyvnmqucokatzsyuahhic";

        BenchMark.run(() -> p.minWindow(s, t));
    }


    static int globalWordId = 1;

    class Word {
        // every word has a unique wordId
        final int wordId;

        final String t;
        final int startIdx;
        int nextIdx = 0;
        int endIdx = -1;

        public Word(String t, int idx, char first) {
            this.wordId = globalWordId++;

            this.t = t;

            this.startIdx = idx;
            nextIdx++;
        }

        final Character getExpectingNextChar() {
            if (nextIdx == t.length()) return '@';
            return t.charAt(nextIdx);
        }

        public boolean addIfOk(int idx, char c) {
            nextIdx++;
            if (complete()) {
                endIdx = idx;
            }
            return true;
        }

        public boolean complete() {
            return this.nextIdx == t.length();
        }

        public int length() {
            if (!complete()) return 0;
            return endIdx - startIdx + 1;
        }
    }

    class Solver {
        final String t;

        int startIdx = -1;
        int endIdx = -1;
        int min = Integer.MAX_VALUE;

        // map next expecting char -> wordId -> word
        final Map<Character, Map<Integer, Word>> charToWordId = new HashMap<>();

        public Solver(String t) {
            this.t = t;
            for (int i=0; i<t.length(); i++) {
                char c = t.charAt(i);
                charToWordId.put(c, new HashMap<>());
            }
        }

        public void consider(int sIdx, char c) {
            if (!charToWordId.containsKey(c)) return;

            // find all words that expecting c as next char
            char curExpectChar = c;
            List<Integer> wordIdsRemove = new ArrayList<>();
            Map<Integer, Word> canWords = charToWordId.get(c);
            for (Word w: canWords.values()) {
                w.addIfOk(sIdx, c);
                char nextExpectChar = w.getExpectingNextChar();

                if (nextExpectChar != curExpectChar) {
                    wordIdsRemove.add(w.wordId);
                    if (!w.complete()) {
                        charToWordId.get(nextExpectChar).put(w.wordId, w);
                    }
                }

                if (w.complete()) {
                    wordIdsRemove.add(w.wordId);
                    if (w.length() < min) {
                        min = w.length();
                        startIdx = w.startIdx;
                        endIdx = w.endIdx;
                    }
                }
            }

            // remove words to remove
            for (int wordId: wordIdsRemove) {
                canWords.remove(wordId);
            }

            // case 1: if c is first char in t
            if (c == t.charAt(0)) {
                Word w = new Word(t, sIdx, c);
                charToWordId.get(w.getExpectingNextChar()).put(w.wordId, w);
            }
        }
    }

    public String minWindow(String s, String t) {
        // worry edge cases later
        Solver solver = new Solver(t);

        if (s.isEmpty()) return "";
        if (s.length() < t.length()) return "";
        if (t.isEmpty()) return "";
        if (t.length()==1) return s.contains(t) ?String.valueOf(t.charAt(0)) :"" ;

        for (int i=0; i<s.length(); i++) {
            char c = s.charAt(i);
            solver.consider(i, c);
        }

        if (solver.startIdx == -1) return "";
        return s.substring(solver.startIdx, solver.endIdx+1);
    }
}
